import { Card, CardBody, CardTitle, CardText, ListGroup, ListGroupItem } from "reactstrap"

    const ProCardItem = ({ data }) => {

        return (
           <div >
                {
                    (data.length < 1) ? (
                        <h1> No hay datos disponibles </h1>
                    )
                    :
                    (

                            data.map((item) => (
                           
                            <Card key={ item.Id } style={{ width: '18rem' }}>
                                <img
                                            alt="Card"
                                            width='100%'
                                    src="https://picsum.photos/300/200" />

                                        <CardBody style={ { textAlign: "center" } }>
                                    <CardTitle tag="h2">{item.nombre}</CardTitle>
                                    <CardText> {item.profesion } </CardText>
                                </CardBody>

                                <ListGroup flush>
                                    <ListGroupItem> {item.horario}</ListGroupItem>
                                    <ListGroupItem> {item.direccion } </ListGroupItem>
                                </ListGroup>

                            </Card>
                            
                        ))
                    )
                }
            </div>
        )
}

    export default ProCardItem;
